mapper.select:
select t.* from (
select clevel,count(1) as num, concat(clevel , ' 级代理') as name  from tb_hy_user_relation 
where userno in (select userno  from tb_hy_user where orgtype =1)

			and clevel > #{clevel}


			and uno1 = #{uno1}


			and uno2 = #{uno2}


			and uno3 = #{uno3}


			and uno4 = #{uno4}


			and uno5 = #{uno5}


			and uno6 = #{uno6}


			and uno7 = #{uno7}


			and uno8 = #{uno8}


			and uno9 = #{uno9}


			and uno10 = #{uno10}


			and uno11 = #{uno11}


			and uno12 = #{uno12}


			and uno13 = #{uno13}


			and uno14 = #{uno14}

group by CLEVEL order by clevel
) t
union all 
select '-1' as clevel,count(1) as num,'全部会员' as name from  tb_hy_user_relation 
where userno in (select userno  from tb_hy_user where orgtype =2)

			and uno1 = #{uno1}


			and uno2 = #{uno2}


			and uno3 = #{uno3}


			and uno4 = #{uno4}


			and uno5 = #{uno5}


			and uno6 = #{uno6}


			and uno7 = #{uno7}


			and uno8 = #{uno8}


			and uno9 = #{uno9}


			and uno10 = #{uno10}


			and uno11 = #{uno11}


			and uno12 = #{uno12}


			and uno13 = #{uno13}


			and uno14 = #{uno14}


		union all 
		select '-2' as clevel,count(1) as num,'直属会员' as name from  tb_hy_user_relation 
		where userno in (select userno  from tb_hy_user where orgtype =2)
		and parentno =#{userno}

select coalesce(sum(COALESCE(t3.ENABLE,0)),0) as enable from (
	SELECT USERNO, max( FDATE ) AS FDATE FROM tb_zj_usermoney GROUP BY USERNO
) t2
LEFT JOIN tb_zj_usermoney t3 ON t2.FDATE = t3.FDATE and t2.userno = t3.userno
left join tb_hy_user_relation t5 on t2.userno = t5.userno


		and t5.uno1 = #{uno1}


		and t5.uno2 = #{uno2}


		and t5.uno3 = #{uno3}


		and t5.uno4 = #{uno4}


		and t5.uno5 = #{uno5}


		and t5.uno6 = #{uno6}


		and t5.uno7 = #{uno7}


		and t5.uno8 = #{uno8}


		and t5.uno9 = #{uno9}


		and t5.uno10 = #{uno10}


		and t5.uno11 = #{uno11}


		and t5.uno12 = #{uno12}


		and t5.uno13 = #{uno13}


		and t5.uno14 = #{uno14}
